import React, { useState } from 'react';
import './App.css';

// Header as a Functional Component
const Header = () => (
  <h2>Окара Сергій Олександрович</h2>
);

// Content as a Class Component
class Content extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFirstElementClicked: false,
      isSecondElementClicked: false,
    };
  }

  toggleFirstElement = () => {
    this.setState((prevState) => ({
      isFirstElementClicked: !prevState.isFirstElementClicked,
    }));
  };

  toggleSecondElement = () => {
    this.setState((prevState) => ({
      isSecondElementClicked: !prevState.isSecondElementClicked,
    }));
  };

  render() {
    return (
      <div>
        <p
          id="firstElement"
          className={`first-element ${this.state.isFirstElementClicked ? 'first-clicked' : ''}`}
          onClick={this.toggleFirstElement}
        >
          Місце та дата народження: м.Охтирка, 9 травня 2005 року
        </p>

        <p
          className={`secondElement ${this.state.isSecondElementClicked ? 'second-clicked' : ''}`}
          onClick={this.toggleSecondElement}
        >
          Освіта: Школа №5, м.Охтирка;
        </p>

        <p style={{ textIndent: '7ch' }}>НТУУ "КПІ", м.Київ</p>
        <p>Хоббі:</p>
        <ul>
          <li>Відеоігри</li>
          <li>Кулінарія</li>
          <li>Спорт</li>
          <li>Паперове моделювання</li>
        </ul>
        <p>Улюблені фільми:</p>
        <ol>
          <li>"Загадкова історія Бенджаміна Баттона", 2008</li>
          <li>"Поки не зіграв у ящик", 2007</li>
          <li>"Завжди кажи «ТАК»", 2008</li>
          <li>"Трикутник смутку", 2022</li>
        </ol>
        <p>
          Київ — столиця та найбільше місто України. Розташований у середній течії Дніпра, у північній Наддніпрянщині.
          Політичний, соціально-економічний, транспортний, освітньо-науковий, історичний, культурний та духовний центр
          України.
        </p>
      </div>
    );
  }
}

// GoodsCard Component
const GoodsCard = ({ image, title, price }) => (
  <div className="goods-card">
    <img 
      src={image} 
      alt={title} 
      className="goods-card-image" 
      style={{
        width: '150px', 
        height: '150px', 
        objectFit: 'cover', 
        objectPosition: 'center'
      }} 
    />
    <div className="goods-card-details">
      <h3 className="goods-card-title">{title}</h3>
      <p className="goods-card-price">{price} грн</p>
      <button className="goods-card-button">Купити</button>
    </div>
  </div>
);

// GoodsGallery Component
const GoodsGallery = () => {
  const goodsData = [
    { id: 1, image: "data:image/jpeg;base64,", title: "Смартфон Apple iPhone 13", price: 25999 },
    { id: 2, image: "data:image/jpeg;base64,", title: "Ноутбук Lenovo IdeaPad", price: 19500 },
    { id: 3, image: "data:image/jpeg;base64,", title: "Бездротові навушники Sony", price: 4999 },
    { id: 4, image: "data:image/jpeg;base64,", title: "Смарт-годинник Samsung", price: 6299 },
    { id: 5, image: "data:image/jpeg;base64,", title: "Планшет iPad Pro", price: 34999 },
    { id: 6, image: "data:image/jpeg;base64,", title: "Цифрова камера Canon", price: 15500 },
  ];

  return (
    <div className="goods-gallery">
      <h2 className="goods-gallery-title">Наші товари</h2>
      <div className="goods-gallery-grid">
        {goodsData.map((item) => (
          <GoodsCard key={item.id} image={item.image} title={item.title} price={item.price} />
        ))}
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [imageHeight, setImageHeight] = useState(500);
  const [isImageVisible, setIsImageVisible] = useState(false);

  const addImage = () => {
    if (!isImageVisible) {
      setIsImageVisible(true);
    }
  };

  const removeImage = () => {
    setIsImageVisible(false);
    setImageHeight(500);
  };

  const increaseImage = () => {
    setImageHeight((prevHeight) => prevHeight + 50);
  };

  const decreaseImage = () => {
    setImageHeight((prevHeight) => Math.max(50, prevHeight - 50));
  };

  return (
    <div className="App">
      <Header />
      <Content />

      {isImageVisible && (
        <a href="https://kyivcity.gov.ua">
          <img
            id="cityImage"
            src="data:image/jpeg;base64,"
            alt="Київ"
            height={imageHeight}
            title="https://kyivcity.gov.ua"
          />
        </a>
      )}

      <div className="button-container">
        <button id="addImageBtn" onClick={addImage}>Додати зображення</button>
        <button id="increaseImageBtn" onClick={increaseImage}>Збільшити</button>
        <button id="decreaseImageBtn" onClick={decreaseImage}>Зменшити</button>
        <button id="removeImageBtn" onClick={removeImage}>Видалити зображення</button>
      </div>

      <GoodsGallery />
    </div>
  );
};

export default App;